SELECT U.USER_ID, U.NICKNAME, CONCAT(CITY, ' ', U.STREET_ADDRESS1, ' ', U.STREET_ADDRESS2) AS 전체주소, CONCAT(SUBSTRING(TLNO FROM 1 FOR 3), '-', SUBSTRING(TLNO FROM 4 FOR 4), '-', SUBSTRING(TLNO FROM 8 FOR 4)) FROM USED_GOODS_USER AS U INNER JOIN (SELECT WRITER_ID, COUNT(WRITER_ID) AS COUNT FROM USED_GOODS_BOARD GROUP BY WRITER_ID) AS B ON U.USER_ID = B.WRITER_ID WHERE B.COUNT >= 3 ORDER BY U.USER_ID DESC